import React, { useState} from 'react';

const API_URL = 'http://localhost:4000/contacts';

const styles = {
  container: {
    maxWidth: 420,
    margin: '40px auto',
    padding: 32,
    background: '#fff',
    borderRadius: 12,
    boxShadow: '0 4px 24px rgba(0,0,0,0.08)',
    fontFamily: 'Segoe UI, Arial, sans-serif'
  },
  heading: {
    textAlign: 'center',
    marginBottom: 24,
    color: '#2d3748'
  },
  form: {
    display: 'flex',
    gap: 8,
    marginBottom: 20
  },
  input: {
    flex: 1,
    padding: '8px 12px',
    borderRadius: 6,
    border: '1px solid #cbd5e1',
    fontSize: 16
  },
  button: {
    padding: '8px 16px',
    borderRadius: 6,
    border: 'none',
    background: '#2563eb',
    color: '#fff',
    fontWeight: 500,
    cursor: 'pointer',
    transition: 'background 0.2s'
  },
  list: {
    listStyle: 'none',
    padding: 0,
    margin: 0
  },
  listItem: {
    padding: '10px 0',
    borderBottom: '1px solid #e2e8f0',
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center'
  },
  contactName: {
    fontWeight: 500,
    color: '#334155'
  },
  contactEmail: {
    color: '#64748b',
    fontSize: 14
  }
};

// ...existing code...

function App() {
  const [contacts, setContacts] = useState([]);
  const [search, setSearch] = useState('');
  const [newName, setNewName] = useState('');
  const [newEmail, setNewEmail] = useState('');
  const [showContacts, setShowContacts] = useState(false);

  const fetchContacts = async (name = '') => {
    const url = name ? `${API_URL}?name=${encodeURIComponent(name)}` : API_URL;
    const res = await fetch(url);
    const data = await res.json();
    setContacts(data);
  };

  // Remove useEffect for initial fetch

  const handleViewContacts = () => {
    fetchContacts();
    setShowContacts(true);
  };

  const handleSearch = (e) => {
    e.preventDefault();
    fetchContacts(search);
    setShowContacts(true);
  };

  const handleAddContact = async (e) => {
    e.preventDefault();
    if (!newName || !newEmail) return;
    await fetch(API_URL, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ name: newName, email: newEmail }),
    });
    setNewName('');
    setNewEmail('');
    fetchContacts();
    setShowContacts(true);
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>Contact List</h2>
      <button style={styles.button} onClick={handleViewContacts}>View Contacts</button>
      <form onSubmit={handleSearch} style={styles.form}>
        <input
          type="text"
          placeholder="Search by name"
          value={search}
          onChange={e => setSearch(e.target.value)}
          style={styles.input}
        />
        <button type="submit" style={styles.button}>Search</button>
      </form>
      <form onSubmit={handleAddContact} style={styles.form}>
        <input
          type="text"
          placeholder="Name"
          value={newName}
          onChange={e => setNewName(e.target.value)}
          required
          style={styles.input}
        />
        <input
          type="email"
          placeholder="Email"
          value={newEmail}
          onChange={e => setNewEmail(e.target.value)}
          required
          style={styles.input}
        />
        <button type="submit" style={styles.button}>Add</button>
      </form>
      {showContacts && (
        <ul style={styles.list}>
          {contacts.map(c => (
            <li key={c.id} style={styles.listItem}>
              <span style={styles.contactName}>{c.name}</span>
              <span style={styles.contactEmail}>{c.email}</span>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default App;
